import React,{useState} from "react"
import "../index.css"

import useInput from "../hooks/InputHooks";
const SimpleInput = (props) => {

   const {value,valueIsValid,InputIsINValid,
    changeHandler,
    BlurHandler,
    Reset}=useInput((value)=>value.trim()!=="")

   let formIsValid=false
   if(valueIsValid){
    formIsValid=true
   }
   const submitHandler=(event)=>{
     event.preventDefault();
     if(valueIsValid)
      Reset()
   }
   const nameInputIsInvalid = InputIsValid;

  const nameInputClasses = nameInputIsInvalid 
    ? 'form-control invalid'
    : 'form-control';

return (
    <form onSubmit={submitHandler}>
      <div className={nameInputClasses}>
        <label htmlFor='name'>Your Name</label>
        <input  type='text' id='name' value={value} onChange={changeHandler} onBlur={BlurHandler}/>
        { InputIsValid && <p>name should not be empty</p>}
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
