import React from 'react';

import MoviesList from './components/MoviesList';
import './App.css';

function App() {

 const [movies,setMovies]= React.useState([])
 const [load,setLoad]=React.useState(false)
 const [error,seterror]=React.useState(null)
  async function  getMovies(){
      setLoad(true)
      seterror(null)
      try{
        let response=await fetch("https://swapi.dev/api/film")
        if(!response.ok){
           seterror("Some thing went wrong")
        }
        let data=await response.json()

          const fetchedData=data.results.map((item)=>{
             return{
               id:item.episode_id,
               title:item.title,
               releaseDate:item.release_date,
               openingText:item.opening_crawl
             }
          })
          setMovies(fetchedData)
      }
      catch(error){
        throw new Error(error.message)
      }
      setLoad(false)

  }

  return (
    <React.Fragment>
      <section>
        <button onClick={getMovies}>Fetch Movies</button>
      </section>
      <section>
       {!load && movies.length>0 && <MoviesList movies={movies} />}
       { load && <p>loading ....</p>}
       {!load && error && <p>some hting went wrong</p>}
       {!load && movies.length===0 && <p>no movies found</p>}
      </section>
    </React.Fragment>
  );
}

export default App;
